#
# Name of the unit system
#
unitsystem = "CGS"
#
# Some natural and astronomy constants
#
au  = 1.49598e13     # Astronomical Unit       [cm]
pc  = 3.08572e18     # Parsec                  [cm]
ms  = 1.98892e33     # Solar mass              [g]
ts  = 5.78e3         # Solar temperature       [K]
ls  = 3.8525e33      # Solar luminosity        [erg/s]
rs  = 6.96e10        # Solar radius            [cm]
gg  = 6.67408e-8     # Gravitational constant  [cm^3/g/s^2]
GG  = 6.67408e-8     # Gravitational constant  [cm^3/g/s^2]
sigma_SB = 5.67e-5   # Stefan-Boltzmann constant
mp  = 1.6726e-24     # Mass of proton          [g]
kk  = 1.3807e-16     # Bolzmann's constant     [erg/K]
mu  = 2.3*mp         # Mean Molecular Weight   [g]
mu_atom = 1.27*mp    # Mean Atomic Weight      [g]
hh  = 6.626e-27      # Planck's constant       [erg.s]
eV  = 1.602e-12      # Electronvolt            [erg]
clum = 2.998e10      # (ask Maximum or Uma what is this constant)
